INSERT INTO `t_cms_content_news` VALUES (12250, '<p>为发挥人大立法主导作用，推动落实省委关于辽河污染防治立法的决策部署，2019年2月15日上午，省人大环境与资源保护委员会在省人大机关组织召开了辽河流域污染防治立法专项工作推进会。省人大常委会副主任张焕秋出席会议</p>\r\n', 'http://192.168.1.118/res/0ac5906539ab4aa9aa7548477971ed66.png');
